import React, { useEffect, useState } from 'react';
import { HStack, Button, Flex, Heading, Input, InputGroup, InputRightElement } from '@chakra-ui/react'
import { useNavigate, useParams } from 'react-router-dom';
import './ChatWindow.css'


function ChatWindow() {
    let lastSender = '';
    const { id } = useParams();
    const [messages, setMessages] = useState([{}])

    async function fetchMessages() {
        const response = await fetch(`/groups/message/${id}`);
        const messages = await response.json();
        setMessages(messages.data)
    }


    useEffect(() => {
        fetchMessages();
    }, [])


    // Navigation onClick functions

    let navigate = useNavigate();

    const redirectDataAnalytics = () => {
        return navigate("/data-analytics")
    }

    const redirectTextChat = () => {
        return navigate("/text-chat")
    }


    const send = async (e) => {
        e.preventDefault();
        const userId = parseInt(sessionStorage.getItem('userId'));
        const messageText = document.getElementById('message').value;

        // Only send if message is not empty
        if (messageText.trim() !== '') {

            const messageData = {
                Contents: messageText,
                Group_idGroup: id,
                Sender: userId,
            };

            await fetch('/messages', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(messageData),
            });

            lastSender = userId;
            document.getElementById('message').focus();
        }
    }

    const back = () => {
        return navigate('/text-chat')
    }

    const settings = () => {
        alert("settings")
    }

    // event listeners for opening and closing settings button
    /*
        document.getElementById('settingsButton').addEventListener('click', function () {
            document.querySelector('.settings-overlay').classList.remove('hidden');
            const updateChatNameButton = document.getElementById('updateChatName');
            const chatNameInput = document.getElementById('chatName');
            const chatTitle = document.querySelector('header h1');
    
            updateChatNameButton.addEventListener('click', () => {
                const newChatName = chatNameInput.value.trim();
                if (newChatName) {
                    chatTitle.textContent = newChatName;
                }
            });
    
            document.getElementById('closeSettings').addEventListener('click', function () {
                document.querySelector('.settings-overlay').classList.add('hidden');
            });
    
            document.getElementById('backButton').addEventListener('click', function () {
                window.location.href = 'Chat_creation.html';
            });
        });
        */

    return (
        <div className='wrapper'>
            <header>
                <HStack justify="center">
                    <Button onClick={redirectDataAnalytics}>Data Analytics</Button>
                    <Button onClick={redirectTextChat}>Text Chat</Button>
                </HStack>
            </header>
            <Flex flexDir='column'>
                <Flex px='1em' justify='space-between'>
                    <Button onClick={back}>Back</Button>
                    <Heading>Group Chat {id}</Heading>
                    <Button onClick={settings}>Settings</Button>
                </Flex>
                <div id='chatMessages' class='messages'>
                    
                </div>
            </Flex>
            <InputGroup
                size='md'
                position='absolute'
                bottom='0'>
                <Input
                    pr='4.5rem'
                    type='text'
                    placeholder='Type your message'
                    id='message' />
                <InputRightElement width='4.5rem'>
                    <Button h='1.75rem' size='sm' onClick={send}>
                        Send
                    </Button>
                </InputRightElement>
            </InputGroup>
        </div>
    );
}

export default ChatWindow;
