import React, { useEffect, useState } from 'react';
import { HStack, Button, Flex, Heading, Input, InputGroup, InputRightElement, useDisclosure, Stack, List, ListItem, LinkBox, LinkOverlay } from '@chakra-ui/react'
import {
    Modal,
    ModalOverlay,
    ModalContent,
    ModalHeader,
    ModalFooter,
    ModalBody,
    ModalCloseButton,
} from '@chakra-ui/react'
import { Select } from 'chakra-react-select'
import { useNavigate, useParams } from 'react-router-dom';
import './ChatWindow.css'



function ChatWindow() {
    const [groupInfo, setGroup] = useState([{}])
    const [groups, setGroups] = useState([{}])
    const [messages, setMessages] = useState([{}])
    const [addSelectOptions, setAddEmployees] = useState([{}]);
    const [selectedAdd, setAddSelected] = useState([{}]);
    const [removeSelectOptions, setRemoveEmployees] = useState([{}])
    const [selectedRemove, setRemoveSelected] = useState([{}])


    let lastSender = '';
    const { id } = useParams();
    const { isOpen, onOpen, onClose } = useDisclosure()

    function containsObject(obj, list) {
        var i;
        for (i = 0; i < list.length; i++) {
            if (list[i].idUser === obj.idUser) {
                return true;
            }
        }

        return false;
    }

    async function fetchGroups() {
        try {
            const groups = await fetch(`/users/${sessionStorage.getItem('userId')}/groups`);
            const groupsData = await groups.json();

            const privateChats = await fetch(`/users/${sessionStorage.getItem('userId')}/private`);
            const privateData = await privateChats.json();

            setGroups(groupsData.data.concat(privateData.data));
            console.log(groupsData.data.concat(privateData.data))
        } catch (error) {
            console.error('Error fetching groups:', error);
        }
    }

    async function fetchGroupInfo() {
        try {
            const groupInfo = await (await fetch(`/groups/${id}`)).json();
            console.log(groupInfo.data)
            setGroup(groupInfo.data)
        } catch (error) {
            console.error('Error fetching group info:', error)
        }
    }

    async function fetchAllUsers() {
        try {
            const allUsers = await (await fetch('/users')).json();
            const groupUsers = await (await fetch(`/groups/${id}/users`)).json();
            const emps = [];
            allUsers.data.forEach((item) => {
                if (!containsObject(item, groupUsers.data)) {
                    emps.push({
                        value: item.idUser,
                        label: item.email
                    })
                }
            })
            setAddEmployees(emps);

        } catch (error) {
            console.error('Error fetching users:', error);
        }
    }

    async function fetchMessages() {
        const response = await fetch(`/groups/${id}/messages`);
        const messages = await response.json();
        setMessages(messages.data)
    }

    async function fetchGroupUsers() {
        try {
            const response = await fetch(`/groups/${id}/users`);
            const data = await response.json();
            const emps = [];
            data.data.forEach((item) => {
                if (parseInt(sessionStorage.getItem('userId')) !== item.idUser) {
                    emps.push({
                        value: item.idUser,
                        label: item.email
                    })
                }
            })
            setRemoveEmployees(emps);

        } catch (error) {
            console.error('Error fetching users:', error);
        }
    }


    useEffect(() => {
        fetchGroups();
        fetchGroupInfo();
        fetchGroupUsers();
        fetchAllUsers();
        fetchMessages();
    }, [])


    // Navigation onClick functions

    let navigate = useNavigate();

    const redirectDataAnalytics = () => {
        return navigate("/data-analytics")
    }

    const redirectTextChat = () => {
        return navigate("/text-chat")
    }


    const send = async (e) => {
        e.preventDefault();
        const userId = parseInt(sessionStorage.getItem('userId'));
        const messageText = document.getElementById('message').value;

        // Only send if message is not empty
        if (messageText.trim() !== '') {

            const messageData = {
                Contents: messageText,
                Group_idGroup: id,
                Sender: userId,
            };


            await fetch('/messages', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(messageData),
            });


            document.getElementById('message').focus();
            fetchMessages();
        }
    }

    const handleChange = (selectedOptions) => {
        setAddSelected(selectedOptions);
    }

    const addUser = async (e) => {
        e.preventDefault();
        let participantIds = []
        selectedAdd.forEach((item) => {
            participantIds.push(item.value)
        })
        try {
            await Promise.all(participantIds.map(async (newId) => {
                const response = await fetch('/membership', {
                    method: 'POST',
                    headers: {
                        'Content-Type': 'application/json'
                    },
                    body: JSON.stringify({ userId: parseInt(newId), groupId: parseInt(id) })
                });

                if (!response.ok) {
                    console.error('Error while creating membership:', await response.text());
                }
            }));
        } catch (error) {
            console.error('Error while creating membership:', error);
        }
    }

    const deleteUser = async (e) => {
        e.preventDefault();
        let participantIds = []
        selectedRemove.forEach((item) => {
            participantIds.push(item.value)
        })
        try {
            await Promise.all(participantIds.map(async (newId) => {
                const response = await fetch('/membership', {
                    method: 'DELETE',
                    headers: {
                        'Content-Type': 'application/json'
                    },
                    body: JSON.stringify({ userId: parseInt(newId), groupId: parseInt(id) })
                });

                if (!response.ok) {
                    console.error('Error while creating membership:', await response.text());
                }
            }));
        } catch (error) {
            console.error('Error while creating membership:', error);
        }
    }

    const leaveGroup = async (e) => {
        e.preventDefault();
        try {
            const response = await fetch('/membership', {
                method: 'DELETE',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({ userId: parseInt(sessionStorage.getItem('userId')), groupId: parseInt(id) })
            });

            if (!response.ok) {
                console.error('Error while creating membership:', await response.text());
            }

        } catch (error) {
            console.error('Error while creating membership:', error);
        }
    }

    const back = () => {
        return navigate('/text-chat')
    }

    return (
        <div className='wrapper'>
            <Modal isOpen={isOpen} onClose={onClose}>
                <ModalOverlay />
                <ModalContent>
                    <ModalHeader><Heading>Settings</Heading></ModalHeader>
                    <ModalCloseButton />
                    <ModalBody>
                        <Stack>

                            <InputGroup>
                                <Input
                                    type='text'
                                    placeholder='Rename chat'
                                    id='nameInput' />
                                <InputRightElement width='4.5rem'>
                                    <Button onClick={send}>
                                        Rename
                                    </Button>
                                </InputRightElement>
                            </InputGroup>

                            <Select
                                closeMenuOnSelect={false}
                                selectedOptionColorScheme="purple"
                                isMulti
                                name="employee-select"
                                options={addSelectOptions}
                                onChange={handleChange}
                                placeholder='Select Employees to add' />
                            <Button onClick={addUser}>
                                Add user
                            </Button>
                            <Select
                                closeMenuOnSelect={false}
                                selectedOptionColorScheme="purple"
                                isMulti
                                name="employee-select"
                                options={removeSelectOptions}
                                onChange={handleChange}
                                placeholder='Select Employees to remove' />
                            <Button onClick={deleteUser}>
                                Remove user
                            </Button>
                        </Stack>
                    </ModalBody>

                    <ModalFooter>
                        <HStack>
                            <Button colorScheme='red' onClick={leaveGroup}>
                                Leave Group
                            </Button>
                            <Button colorScheme='blue' onClick={onClose}>
                                Close
                            </Button>
                        </HStack>
                    </ModalFooter>
                </ModalContent>
            </Modal>
            <Flex flexDir={'column'} bg='green.500' justify='center' gap='10px'>
                <Button onClick={redirectDataAnalytics}>Data</Button>
                <Button onClick={redirectTextChat}>Chat</Button>
            </Flex>
            <Flex flexDir='column' border='1px' borderColor='gray.200' flexGrow='0.1'>
                <List>
                    {groups.map((item) => (
                        <ListItem key={item} >
                            <LinkBox as='article' p='3' borderWidth='1px' _hover={{ color: "teal.500" }}>
                                <Heading size='md'>
                                    <LinkOverlay href={item.idGroup}>
                                        {item.Name}
                                    </LinkOverlay>
                                </Heading>
                            </LinkBox>
                        </ListItem>
                    ))}
                </List>
            </Flex>
            <Flex flexDir='column'>
                <Flex px='1em' justify='space-between'>
                    <Button onClick={back}>Back</Button>
                    <Heading>{groupInfo[0].Name}</Heading>
                    <Button onClick={onOpen}>Settings</Button>
                </Flex>
                <div id='chatMessages' className='messages'>
                    {messages.map((message) => {
                        const userId = parseInt(sessionStorage.getItem('userId'));
                        const isCurrentUser = message.Sender === userId;
                        if (lastSender !== message.Sender) {
                            lastSender = message.Sender;
                            return (
                                <div key={message.idMessages} className='message-wrapper'>
                                    <div className={isCurrentUser ? 'username user-username' : 'username'}>
                                        {message.Name}
                                    </div>
                                    <div className={isCurrentUser ? 'message user-message' : 'message'}>
                                        {message.Contents}
                                    </div>
                                </div>
                            )
                        } else {
                            lastSender = message.Sender;
                            return (
                                <div key={message.idMessages} className='message-wrapper'>
                                    <div className={isCurrentUser ? 'message user-message' : 'message'}>
                                        {message.Contents}
                                    </div>
                                </div>
                            )
                        }

                    })}
                </div>
                <InputGroup
                size='md'
                position='absolute'
                bottom='0'>
                <Input
                    pr='4.5rem'
                    type='text'
                    placeholder='Type your message'
                    id='message' />
                <InputRightElement width='4.5rem'>
                    <Button h='1.75rem' size='sm' onClick={send}>
                        Send
                    </Button>
                </InputRightElement>
            </InputGroup>
            </Flex>
            
        </div>
    );
}

export default ChatWindow;
