import React, { useEffect, useState } from "react";
import { Flex, List, LinkBox, Heading, IconButton, ListItem } from "@chakra-ui/react"
import { CloseIcon } from "@chakra-ui/icons";
import { Link } from "react-router-dom";

function Groups() {
    const [groups, setGroups] = useState([{}])
    const [updatedGroups, setUpdatedGroups] = useState()


    async function fetchGroups() {
        try {
            const groups = await fetch(`/users/${sessionStorage.getItem('userId')}/groups`);
            const groupsData = await groups.json();

            const privateChats = await fetch(`/users/${sessionStorage.getItem('userId')}/private`);
            const privateData = await privateChats.json();

            setGroups(groupsData.data.concat(privateData.data));
        } catch (error) {
            console.error('Error fetching groups:', error);
        }
    }

    useEffect(() => {
        fetchGroups();
    }, [updatedGroups])

    const deleteGroup = async (id) => {
        try {
            await fetch(`/groups/${id}`, {
                method: 'DELETE'
            });

            setUpdatedGroups(!updatedGroups)
        } catch (error) {
            console.error('Error fetching groups:', error);
        }
    }

    return (
        <Flex flexDir='column' bg='#3C3C3D' flexBasis='400px'>
            <List>
                {groups.map((item) => {
                    const link = `${item.idGroup}`
                    return (
                        <ListItem>
                            <LinkBox as='article' p='5' >
                                <Heading size='md' my='2' color='whiteAlpha.900'>
                                    <Link to={link}>
                                        {item.Name}
                                    </Link>
                                </Heading>
                                <IconButton ml='auto' mr='0' color='whiteAlpha.700' bg='' aria-label='Delete Group' icon={<CloseIcon />} onClick={() => deleteGroup(item.idGroup)} _hover={{ color: 'white', backgroundColor: '' }} />

                            </LinkBox>
                        </ListItem>
                    )
                })}
            </List>
        </Flex>
    )
}

export default Groups;
