import React, { useEffect, useState } from 'react';
import { HStack, Button, Container, FormControl, FormLabel, Heading, Stack, List, ListItem, LinkBox, LinkOverlay } from '@chakra-ui/react'
import { Select } from 'chakra-react-select'
import { useNavigate } from 'react-router-dom';
import './App.css'


function TextChat() {

    // States and API calls to update states on load

    const [employeeSelectOptions, setEmployees] = useState([{}]);
    const [groups, setGroups] = useState([{}]);
    const [selected, setSelected] = useState([{}]);

    useEffect(() => {
        async function fetchAllUsers() {
            try {
                const response = await fetch('/users'); // Use the full URL here
                const data = await response.json();
                const emps = [];
                data.data.forEach((item) => {
                    if (sessionStorage.getItem('userId') != item.idUser) {
                        emps.push({
                            value: item.idUser,
                            label: item.email
                        })
                    }
                })
                setEmployees(emps);
            } catch (error) {
                console.error('Error fetching users:', error);
            }
        }

        async function fetchGroups() {
            try {
                const response = await fetch(`/groups/${sessionStorage.getItem('userId')}`);
                const data = await response.json();
                setGroups(data.data);
            } catch (error) {
                console.error('Error fetching groups:', error);
            }
        }
        fetchAllUsers();
        fetchGroups();
    }, [])

    // Onclick functions


    let navigate = useNavigate();
    const redirectDataAnalytics = () => {
        return navigate("/data-analytics")
    }
    const redirectTextChat = () => {
        return navigate("/text-chat")
    }
    const handleChange = (selectedOptions) => {
        setSelected(selectedOptions);
    }
    const createChat = async (e) => {
        e.preventDefault();
        console.log(selected)
        const apiEndpoint = selected.length === 1 ? '/private' : '/groups';
        try {
            const response = await fetch(apiEndpoint, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify(
                    {
                        Name: `New chat`,
                    }
                )
            })
            if (response.status === 200) {
                const responseData = await response.json();
                console.log(responseData);

                // Get the ID of the newly created chat to be passed to membership table
                const chatId = responseData.message.newId;
                console.log('Chat ID1:', chatId);

                // Add each participant to the new group including the loggedInUserId
                await createMembership(participantIds, chatId, loggedInUserId);

                // Refresh the chat list
                const chatGroups = await fetchChatGroups(loggedInUserId);
                const privateMessages = await fetchPrivateMessages(loggedInUserId);
                const combinedChats = await asyncMap(chatGroups, privateMessages);
                displayChats(combinedChats);
            } else {
                console.error('Error creating chat:', response.statusText);
            }
        } catch (error) {
            console.error('Error creating chat:', error)
        }
        /*
        const employees = document.getElementById('employees').selectedOptions;
        const participants = Array.from(employees).map(e => e.value).join(', ');
        const chatList = document.getElementById('chatList');

        const newChat = document.createElement('li');
        newChat.textContent = `Chat with ${participants}`;
        newChat.addEventListener('click', function () {
            // TODO
            window.location.href = `chat_instance.html?chatId=${Date.now()}`;
        });

        chatList.appendChild(newChat);
        */
    }

    return (
        <div className="App">
            <header>
                <HStack justify="center">
                    <Button onClick={redirectDataAnalytics}>Data Analytics</Button>
                    <Button onClick={redirectTextChat}>Text Chat</Button>
                </HStack>
            </header>
            <Container className='inner progress'>
                <FormControl>
                    <Stack>
                        <FormLabel>
                            <Heading size="md">Select Employees</Heading>
                        </FormLabel>
                        <Select
                            closeMenuOnSelect={false}
                            selectedOptionColorScheme="purple"
                            isMulti
                            name="employee-select"
                            options={employeeSelectOptions}
                            onChange={handleChange}
                            placeholder='Select Employees to add' />
                    </Stack>
                </FormControl>
                <Button onClick={createChat}>Create Chat</Button>
            </Container>
            <Container className='inner progress'>
                <Heading size="md">Existing Chats</Heading>
                <List spacing={3}>
                    {groups.map((item) => (
                        <ListItem key={item}>
                            <LinkBox as='article' p='3' borderWidth='1px' rounded='md' _hover={{ color: "teal.500" }}>
                                <Heading size='md'>
                                    <LinkOverlay href={`text-chat/${item.idGroup}`}>
                                        {item.Name}
                                    </LinkOverlay>
                                </Heading>
                            </LinkBox>
                        </ListItem>
                    ))}
                </List>
            </Container>
        </div >
    );
}

export default TextChat;
