import React, { useEffect, useState } from 'react';
import { HStack, Button, Flex, Heading, Input, InputGroup, InputRightElement, Text, useDisclosure } from '@chakra-ui/react'
import {
    Modal,
    ModalOverlay,
    ModalContent,
    ModalHeader,
    ModalFooter,
    ModalBody,
    ModalCloseButton,
} from '@chakra-ui/react'
import { useNavigate, useParams } from 'react-router-dom';
import './ChatWindow.css'



function ChatWindow() {
    const [messages, setMessages] = useState([{}])
    let lastSender = '';
    const { id } = useParams();
    const { isOpen, onOpen, onClose } = useDisclosure()

    async function fetchMessages() {
        const response = await fetch(`/groups/message/${id}`);
        const messages = await response.json();
        setMessages(messages.data)
        console.log(messages.data)
    }

    useEffect(() => {
        fetchMessages();
    }, [])


    // Navigation onClick functions

    let navigate = useNavigate();

    const redirectDataAnalytics = () => {
        return navigate("/data-analytics")
    }

    const redirectTextChat = () => {
        return navigate("/text-chat")
    }


    const send = async (e) => {
        e.preventDefault();
        const userId = parseInt(sessionStorage.getItem('userId'));
        const messageText = document.getElementById('message').value;

        // Only send if message is not empty
        if (messageText.trim() !== '') {

            const messageData = {
                Contents: messageText,
                Group_idGroup: id,
                Sender: userId,
            };


            await fetch('/messages', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(messageData),
            });


            document.getElementById('message').focus();
            fetchMessages();
        }
    }

    const back = () => {
        return navigate('/text-chat')
    }

    const settings = () => {
        alert("settings")
    }

    // event listeners for opening and closing settings button
    /*
        document.getElementById('settingsButton').addEventListener('click', function () {
            document.querySelector('.settings-overlay').classList.remove('hidden');
            const updateChatNameButton = document.getElementById('updateChatName');
            const chatNameInput = document.getElementById('chatName');
            const chatTitle = document.querySelector('header h1');
     
            updateChatNameButton.addEventListener('click', () => {
                const newChatName = chatNameInput.value.trim();
                if (newChatName) {
                    chatTitle.textContent = newChatName;
                }
            });
     
            document.getElementById('closeSettings').addEventListener('click', function () {
                document.querySelector('.settings-overlay').classList.add('hidden');
            });
     
            document.getElementById('backButton').addEventListener('click', function () {
                window.location.href = 'Chat_creation.html';
            });
        });
        */

    return (
        <div className='wrapper'>
            <Modal isOpen={isOpen} onClose={onClose}>
                <ModalOverlay />
                <ModalContent>
                    <ModalHeader>Modal Title</ModalHeader>
                    <ModalCloseButton />
                    <ModalBody>
                        <Text>test</Text>
                    </ModalBody>

                    <ModalFooter>
                        <Button colorScheme='blue' mr={3} onClick={onClose}>
                            Close
                        </Button>
                        <Button variant='ghost'>Secondary Action</Button>
                    </ModalFooter>
                </ModalContent>
            </Modal>
            <header>
                <HStack justify="center">
                    <Button onClick={redirectDataAnalytics}>Data Analytics</Button>
                    <Button onClick={redirectTextChat}>Text Chat</Button>
                </HStack>
            </header>
            <Flex flexDir='column'>
                <Flex px='1em' justify='space-between'>
                    <Button onClick={back}>Back</Button>
                    <Heading>Group Chat {id}</Heading>
                    <Button onClick={settings}>Settings</Button>
                </Flex>
                <div id='chatMessages' className='messages'>
                    {messages.map((message) => {
                        console.log(`Last sender: ${lastSender}`)
                        const userId = parseInt(sessionStorage.getItem('userId'));
                        const isCurrentUser = message.Sender === userId;
                        if (lastSender !== message.Sender) {
                            lastSender = message.Sender;
                            return (
                                <div key={message.idMessages} className='message-wrapper'>
                                    <div className={isCurrentUser ? 'username user-username' : 'username'}>
                                        {message.Sender}
                                    </div>
                                    <div className={isCurrentUser ? 'message user-message' : 'message'}>
                                        {message.Contents}
                                    </div>
                                </div>
                            )
                        } else {
                            lastSender = message.Sender;
                            return (
                                <div key={message.idMessages} className='message-wrapper'>
                                    <div className={isCurrentUser ? 'message user-message' : 'message'}>
                                        {message.Contents}
                                    </div>
                                </div>
                            )
                        }

                    })}
                </div>
            </Flex>
            <InputGroup
                size='md'
                position='absolute'
                bottom='0'>
                <Input
                    pr='4.5rem'
                    type='text'
                    placeholder='Type your message'
                    id='message' />
                <InputRightElement width='4.5rem'>
                    <Button h='1.75rem' size='sm' onClick={send}>
                        Send
                    </Button>
                </InputRightElement>
            </InputGroup>
        </div>
    );
}

export default ChatWindow;
