import React from 'react';
import { HStack, Button, Flex, Heading, Input, InputGroup, InputRightElement } from '@chakra-ui/react'
import { useNavigate, useParams } from 'react-router-dom';
import './ChatWindow.css'


function ChatWindow() {

    let navigate = useNavigate();
    const redirectDataAnalytics = () => {
        return navigate("/data-analytics")
    }
    const redirectTextChat = () => {
        return navigate("/text-chat")
    }
    const { id } = useParams();

    return (
        <div className='wrapper'>
            <header>
                <HStack justify="center">
                    <Button onClick={redirectDataAnalytics}>Data Analytics</Button>
                    <Button onClick={redirectTextChat}>Text Chat</Button>
                </HStack>
            </header>
            <header>
                <button id="backButton" class="settings-button">Back</button>
                <h1>Chat</h1>
                <button id="settingsButton" class="settings-button">Settings</button>
            </header>

            <section class="chat-window">
                <div id="chatMessages" class="messages"></div>
                <form id="sendMessageForm" class="send-message">
                    <input type="text" id="messageText" placeholder="Type your message..." />
                    <button type="submit">Send</button>
                </form>
            </section>

            <div class="settings-overlay hidden">
                <div id="settingsMenu" class="settings-menu hidden">
                    <button id="closeSettings" class="exit-button">&times;</button>
                    <h2>Chat Settings</h2>
                    <div>
                        <label for="chatName">Chat Name:</label>
                        <input type="text" id="chatName" />
                        <button id="updateChatName">Update</button>
                    </div>
                    <h3>Users</h3>
                    <ul id="userList"></ul>
                    <button id="addUser">Add User</button>
                    <button id="leaveChat">Leave Chat</button>
                </div>
            </div>

            <script src="chat_instance.js"></script>
        </div>
    );
}

export default ChatWindow;
